        #region GET Token
        [HttpPost]
        [Route("login")]
        [AllowAnonymous]
        public async Task<ActionResult<dynamic>> Authenticate([FromBody] User model)
        {
            var user = UserRepository.Get(model.Username, model.Password);

            if (user == null)
                return NotFound(new { message = "Usuário ou senha inválidos" });

            var token = TokenService.GenerateToken(user);
            user.Password = "";
            return new
            {
                user = user,
                token = token
            };

        }
        #endregion

        #region POST - Envia texto
        [HttpPost]
        [Route("Post")]
        [Authorize]
        public async Task<IActionResult> EnviaTexto(string texto, [FromServices] IConfiguration configuration)
        {
            var token = "475b1d3fd6d602e768ab139aed1981ea";

            //var token = configuration.GetValue<string>("475b1d3fd6d602e768ab139aed1981ea");

            _httpClient.DefaultRequestHeaders.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue(token);

            var model = new EnviaTextoInput(texto);

            var requestBody = JsonSerializer.Serialize(model);

            var content = new StringContent(requestBody, Encoding.UTF8, "application/json");

            var response = await _httpClient.PostAsync("https://scisummary.com/api/batch", content);

            var result = await response.Content.ReadFromJsonAsync<EnviaTextoOutput>();

            var promptResponse = result.requests.First().uuid;


            return Ok(promptResponse);

        }
        #endregion

